/**
 * Directive to generate the tax table on the bill.  The directive attribute is:
 *    taxes - an object (generated by the Reservation View Model's calculateTotals method.
 *
 * Other business logic:
 *    none
 */
define(['./module'], function (directives) {
  'use strict';
  directives.directive('axBillTaxes', ['configService',
    function (configService) {
      var linker = function (scope, element, attrs) {

        //define which items appear in which section of the bill_dec32

        scope.txt = configService.loctxt;
        scope.tax7 = configService.constants.get('roomTax');
        scope.tax19 = configService.constants.get('salesTax');

      }; //end link function

      return {
        restrict: 'E',
        link: linker,
        templateUrl: './templates/ax-bill-taxes.html',
        scope: {
          taxes: '='
        }
      };
    }]);
});
